model = load_model()

# ----------------------------
# ORIGINAL PREPROCESSING (unchanged)
# ----------------------------
def preprocess_audio(file_path, target_sr=16000):
    y, sr = librosa.load(file_path, sr=target_sr)
    if len(y) > 1024:
        y = y[:1024]
    else:
        y = np.pad(y, (0, max(0, 1024 - len(y))))
    X = np.expand_dims(y, axis=0).astype(np.float32)
    return X

